	$('#sele').live('click',function(){
		$id = $(this).attr('name');
		$power = $('select[name=power'+$id+']').val();
		$.post('adminUp',{'id':$id,'power':$power},function(data){
			if(data==0){
				//修改成功
				$('a[name='+$id+']').next().html("<img src='/Project/Mls/Home/Public/Images/yes.jpg'>");
			}else if(data==1){
				//修改失败				
				$('a[name='+$id+']').next().html("<img src='/Project/Mls/Home/Public/Images/no.jpg'>");
			}else if(data==2){
				//修改失败				
				$('a[name='+$id+']').next().css('color','red').html("你没有权限修改");
			}else if(data ==3){
				$('a[name='+$id+']').next().css('color','red').html("你不能修改自己的权限");
			}
		})
	})
	$('#del').live('click',function(){
		$id = $(this).attr('name');
		$('#save').attr('name',$id);
		$('.modal-title').html('你确定要删除吗?');
		$('#save[name='+$id+']').live('click',function(){
			$.post('delAdmin',{'id':$id},function(data){
				$('#save').attr('name','');//初始化
				if(data == 0){
					$('tr[name='+$id+']').remove();
					$('.modal-title').html('删除成功');
					
				}else if(data == 1){
					$('.modal-title').html('删除失败');

				}
			})
		})
	})



